mapper.insert:

INSERT INTO tbl_qna(	 qna_no
						,qna_title
						,qna_content
						,qna_regist_date
						,qna_write
						,qna_write_name
						,qna_secret

						,qna_password

					)

VALUES				(	 seq_qna.NEXTVAL
						,#{qna_title}
						,#{qna_content}
						,SYSDATE
						,#{qna_write}
						,#{qna_write_name}
						,#{qna_secret}

						,#{qna_password}

					)

mapper.select:
SELECT qna_no ,qna_title ,qna_content ,qna_regist_date ,qna_write ,qna_write_name ,qna_secret ,qna_password
FROM tbl_qna
WHERE qna_no = #{qna_no}

mapper.update:
UPDATE  tbl_qna

SET      qna_title = #{qna_title}
		,qna_content = #{qna_content}

WHERE    qna_no = #{qna_no}

UPDATE   tbl_qna

SET      qna_reply_exist = #{qna_reply_exist}

WHERE    qna_no = #{qna_no}

mapper.select:
SELECT  qna_no

FROM    tbl_qna

WHERE   qna_no = #{qna_no}

AND     qna_password = #{qna_password}
